import React,{useEffect, Fragment} from 'react'
import {connect} from 'react-redux'
import {getCurrentProfile} from '../../actions/profile'
import {Spinner} from '../layout/Spinner'

const DashboardProfile = ({getCurrentProfile,auth:{user},profile:{profile,loading}}) => {
   useEffect(()=>{
       getCurrentProfile()
   },[])

    return loading&&profile==null?<Spinner />:(<Fragment>
        <h1 className='large text-primary'>Dashboard</h1>
        <p className='lead'><i className='fas fa-user'/>Welcome {user&&user.name}</p>
    </Fragment>)
}

const mapStateToProps=state=>({
    auth:state.auth,
    profile:state.profile
})

export default connect(mapStateToProps,{getCurrentProfile}) (DashboardProfile)
