import { GET_POSTS, POST_ERRORS ,UPDATE_LIKES,DELETE_POST} from '../actions/types';
import { post } from 'request';
const intialState = {
  posts: [],
  post: null,
  errors: {},
  loading: true
};

export default function(state = intialState, actions) {
  const { type, payload } = actions;

  switch (type) {
    case GET_POSTS:
      return {
        ...state,
        posts: payload,
        loading: false
      };
    case POST_ERRORS:
      return {
        ...state,
        errors: payload,
        loading: false
      };

      case UPDATE_LIKES:
        return{
          ...state,
          posts:state.posts.map(post=>post._id===payload.postId?{...post,like:payload.likes}:post),
          loading:false
        }

      case DELETE_POST:
        return{
          ...state,
          posts:state.posts.filter(post=>post._id !==payload),
          loading:false
        }
    default:
      return state;
  }
}
