import React, { Fragment, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import './App.css';

//components
import Navbar from './components/layout/Navbar';
import Landing from './components/layout/Landing';

//profile
import DashboardProfile from './components/profile/DashboardProfile';
import CreateProfile from './components/profile/profile-forms/CreateProfile';
import EditProfile from './components/profile/profile-forms/EditProfile';
import AddExperience from './components/profile/profile-forms/AddExperience';
import AddEducation from './components/profile/profile-forms/AddEducation';

//auth
import Login from './components/auth/Login';
import Register from './components/auth/Register';

//redux
import { Provider } from 'react-redux';
import store from './store';
import Alerts from './components/layout/Alerts';

//PrivateRoute
import PrivateRoute from './routing/PrivateRoute';

//settoken
import setAuthtoken from './utils/setAuthtoken';
import { loadUser } from './actions/auth';


if (localStorage.token) {
  setAuthtoken(localStorage.token);
}

function App() {
  useEffect(() => {
    store.dispatch(loadUser());
  }, []);
  return (
    <Provider store={store}>
      <Router>
        <Fragment>
          <Navbar />
          <Route exact path='/' component={Landing} />
          <section className='container'>
            <Alerts />
            <Switch>
              <Route exact path='/login' component={Login} />
              <Route exact path='/register' component={Register} />
              <PrivateRoute
                exact
                path='/dashboard'
                component={DashboardProfile}
              />
              <PrivateRoute
                exact
                path='/create-profile'
                component={CreateProfile}
              />
              <PrivateRoute
                exact
                path='/edit-profile'
                component={EditProfile}
              />
              <PrivateRoute
                exact
                path='/add-experience'
                component={AddExperience}
              />
              <PrivateRoute
                exact
                path='/add-education'
                component={AddEd}
              />
            </Switch>
          </section>
        </Fragment>
      </Router>
    </Provider>
  );
}

export default App;
