import React, { Fragment, useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import AlertContext from '../context/alert/AlertContext';
import AuthContext from '../context/auth/AuthContext';

const Login = props => {
  const { setAlert, alerts, loadUser } = useContext(AlertContext);
  const { login, errors, clearErrors, isAuthenticated } = useContext(
    AuthContext
  );



  useEffect(() => {
    if (isAuthenticated) {
      props.history.push('/dashboard');
    }
    if (errors === 'Invalid Credentials') {
      setAlert(errors, 'danger');
      clearErrors();
    }

    //eslint-disable-next-line
  }, [errors, isAuthenticated, props.history]);


  // //  const { loadUser } = useContext(AuthContext);

  // useEffect(() => {
  //   return loadUser();

  //   //eslint-disable-next-line
  // }, []);

  const [FormData, setFormData] = useState({
    email: '',
    password: ''
  });
  const { email, password } = FormData;
  const onChange = e =>
    setFormData({ ...FormData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    if (email == '' || password == '') {
      setAlert('Please Fill Out All Fields', 'danger');
    } else {
      login({ email, password });
    }
  };
  return (
    <Fragment>
      <h1 className='large text-primary'>Sign In</h1>
      <p className='lead'>
        <i className='fas fa-user'></i> Sign Into Your Account
      </p>
      <form className='form' action='create-profile.html' onSubmit={onSubmit}>
        <div className='form-group'>
          <input
            type='email'
            placeholder='Email Address'
            name='email'
            value={email}
            onChange={onChange}
          />
        </div>
        <div className='form-group'>
          <input
            type='password'
            placeholder='Password'
            name='password'
            minLength='6'
            value={password}
            onChange={onChange}
          />
        </div>

        <input type='submit' className='btn btn-primary' value='Login' />
      </form>
      <p className='my-1'>
        Do'nt have an account? <Link to='/register'>Register</Link>
      </p>
    </Fragment>
  );
};
export default Login;
