import React, { Fragment, useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/auth/AuthContext';
import ProfileContext from '../context/auth/ProfileContext';


const Navbar = () => {
  const { logout, user, isAuthenticated } = useContext(AuthContext);
  const {clearProfile}=useContext(ProfileContext)

  const onLogOut = () => {
    logout();

  };

  const authLink = (
    <ul>
      <li>
        <Link to='/dashboard'><i className='fas fa-user'></i>
          <span className='hide-sm'>DashBoard</span></Link>

        <a href='#' onClick={onLogOut}>
          <i className='fas fa-sign-out-alt'></i>
          <span className='hide-sm'>Logout</span>
        </a>
      </li>
    </ul>
  );

  const guestLink = (
    <Fragment>
      <li>
        <a href='#!'>Developers</a>
      </li>
      <li>
        <Link to='/register'>Register</Link>
      </li>
      <li>
        <Link to='/login'>Login</Link>
      </li>
    </Fragment>
  );

  return (
    <li className='navbar bg-dark'>
      <h1>
        <Link href='/'>
          <i className='fas fa-code'></i> DevConnector
        </Link>
      </h1>
      <ul>{isAuthenticated ? authLink : guestLink}</ul>
    </li>
  );
};

export default Navbar;
