import { GET_PROFILE, PROFILE_ERRORS, CLEAR_PROFILE } from '../types';

export default (state, action) => {
  switch (action.type) {
    case GET_PROFILE:
      return {
        ...state,
        profile: action.payload,
        loading: false
      };
    case PROFILE_ERRORS:
      return {
        ...state,
        error: action.payload,
        loading: false
      };

    case CLEAR_PROFILE:
      return {
        profile: null,
        profiles: [],
        error: {},
        repos: [],
        loading: true
      };
    default:
      return state;
  }
};
