import { GET_POSTS, POST_ERRORS ,UPDATE_LIKES,DELETE_POST,ADD_POST,GET_POST,REMOVE_COMMENT,ADD_COMMENT} from '../actions/types';

const intialState = {
  posts: [],
  post: null,
  errors: {},
  loading: true
};

export default function(state = intialState, actions) {
  const { type, payload } = actions;

  switch (type) {
    case GET_POSTS:
      return {
        ...state,
        posts: payload,
        loading: false
      };
      case GET_POST:
        return {
          ...state,
          post: payload,
          loading: false
        };
    case POST_ERRORS:
      return {
        ...state,
        errors: payload,
        loading: false
      };

      case UPDATE_LIKES:
        return{
          ...state,
          posts:state.posts.map(post=>post._id===payload.postId?{...post,like:payload.likes}:post),
          loading:false
        }

      case DELETE_POST:
        return{
          ...state,
          posts:state.posts.filter(post=>post._id !==payload),
          loading:false
        }

      case ADD_POST:
        return{
          ...state,
          posts:[...state.posts,payload],
          loading:false
        }
        case ADD_COMMENT:
          return{
            ...state,
            posts:state.posts.map(),
          }
    default:
      return state;
  }
}
