import React, { Fragment, useState, useContext ,useEffect} from 'react';
import { Link } from 'react-router-dom';
import AlertContext from '../context/alert/AlertContext';
import AuthContext from '../context/auth/AuthContext';
const Register = () => {
  const { setAlert, alerts } = useContext(AlertContext);

  const { register, errors, clearErrors, isAuthenticated } = useContext(
    AuthContext
  );

  useEffect(() => {
    if (isAuthenticated) {
      props.history.push('/');
    }
    if (errors === 'user already exists') {
      setAlert(errors, 'danger');
      clearErrors();
    }

    //eslint-disable-next-line
  }, [errors, isAuthenticated, props.history]);



  const [FormData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    password2: ''
  });

  const { name, email, password, password2 } = FormData;

  const onChange = e =>
    setFormData({ ...FormData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    if (name == '' || email == '' || password == '') {
      setAlert('Please enter All Fields', 'danger');
    }
     else if (password !== password2) {
      setAlert('Password do not match', 'danger');
    } 
    else if (errors === 'Password should be more than 6 chars') {
      setAlert(errors, 'danger');
    }
     else {
      register({ name, email, password });
    }
  };


  return (
    <Fragment>
      <h1 className='large text-primary'>Sign Up</h1>
      <p className='lead'>
        <i className='fas fa-user'></i> Create Your Account
      </p>
      <form className='form' action='create-profile.html' onSubmit={onSubmit}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='Name'
            name='name'
            value={name}
            onChange={onChange}
          />
        </div>
        <div className='form-group'>
          <input
            type='email'
            placeholder='Email Address'
            name='email'
            value={email}
            onChange={onChange}
          />
          <small className='form-text'>
            This site uses Gravatar so if you want a profile image, use a
            Gravatar email
          </small>
        </div>
        <div className='form-group'>
          <input
            type='password'
            placeholder='Password'
            name='password'
            value={password}
            onChange={onChange}
          />
        </div>
        <div className='form-group'>
          <input
            type='password'
            placeholder='Confirm Password'
            name='password2'
            value={password2}
            onChange={onChange}
          />
        </div>
        <input type='submit' className='btn btn-primary' value='Register' />
      </form>
      <p className='my-1'>
        Already have an account? <Link to='/login'>Sign In</Link>
      </p>
    </Fragment>
  );
};

export default Register;
