import React, { useContext,useEffect, Fragment } from 'react';
import ProfileContext from '../context/profile/ProfileContext';
import {Spinner} from '../layout/Spinner'
import AuthContext from '../context/auth/AuthContext'
const Dashboard = () => {
  const { getCurrentPorfile,loading,profile } = useContext(ProfileContext);
  const {user}=useContext(AuthContext)

  useEffect(() => {
    getCurrentPorfile();
    // eslint-disable-next-line
  }, []);
  return  loading&&profile ==null?<Spinner/>:<Fragment><h1 className='large text-primary'>
    </h1><p className='lead'><i className='fas fa-user'></i>Welcome{user&&user.name}
      </p></Fragment>;
};

export default Dashboard;
