import {GET_POSTS,POST_ERRORS} from '../actions/types'
const intialState={
    posts:[],
    post:null,
    errors:{},
    loading:true
}

export default function(state=intialState,actions){
    const{type,payload}=actions

    switch(type){
        case GET_POSTS:
            return{
                ...state,
                posts:payload,
                loading:false
            }
        case POST_ERRORS:
            return{
                ...state,
                errors:payload,
                loading:false
    }
    default: return state;
}}
