import { GET_POSTS, POST_ERRORS ,UPDATE_LIKES} from '../actions/types';
import { post } from 'request';
const intialState = {
  posts: [],
  post: null,
  errors: {},
  loading: true
};

export default function(state = intialState, actions) {
  const { type, payload } = actions;

  switch (type) {
    case GET_POSTS:
      return {
        ...state,
        posts: payload,
        loading: false
      };
    case POST_ERRORS:
      return {
        ...state,
        errors: payload,
        loading: false
      };

      case UPDATE_LIKES:
        return{
          ...state,
          posts:state.posts.map(post._id===payload.id?{...post,like:payload.likes}:post)
        }
    default:
      return state;
  }
}
